validate_doc_update:
// Rules that control what record content can be stored in Couch for the Preference Terms Dictionary
function(newDoc, oldDoc, userCtx) {
    // The user has to be logged in to perform updates
    if (!userCtx || userCtx.name === null) {
        throw({"unauthorized" : {"summary": "You must be logged in to add or edit records."}});
    }
    else {
        // We only allow couch admins to perform actual deletes
        if(newDoc._deleted === true && (!userCtx.roles || userCtx.roles.indexOf("_admin") === -1)) {
            throw({"unauthorized": {"summary" : "Only admins are allowed to directly remove records."}});
        }
    }
}

flat.map:
function(doc) {
    if (doc) {
        var key = doc.uniqueId;
        var type = 0;

        // Don't include operators in the combined view for now.
        if (doc.type === "condition") { return; }
        else if (doc.type === "alias" || doc.type === "transform") { key = doc.aliasOf; type = 1; }
        else if (doc.type === "translation") { key = doc.translationOf; type = 1; }

        emit([key, type], doc);
    }
}

terms.map:
function(doc) {
    if (doc) {
        if (doc && doc.type && doc.type.toLowerCase() == 'term' ) {
            emit(doc.uniqueId, doc);
        }
    }
}

transforms.map:
function(doc) {
    if (doc) {
        if (doc && doc.type && (doc.type.toLowerCase() === 'transform')) {
            emit(doc.uniqueId, doc);
        }
    }
}

